import React from 'react'

const Form = ({input,setInput,todos,setTodos}) => {
  return (
    <form>
        <input type="text" placeholder='Enter a Todo...' className="task-input" value={input} />
        <button className='button-add' type="submit">Add</button>
    </form>
  )
}

export default Form
